mapper.update:
update sky_take_out.setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}, status = #{status},
where id = #{id}

mapper.select:
select s.*,c.name as categoryName from sky_take_out.setmeal as s,sky_take_out.category as c


        and dish.name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and s.status = #{status}

    and category_id = c.id

order by update_time desc
