mapper.update:
update sky_take_out.shopping_cart set number=#{number}


        and user_id = #{userId}


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}

mapper.delete:
delete from sky_take_out.shopping_cart


            and user_id = #{userId}


            and dish_id = #{dishId}


            and setmeal_id = #{setmealId}


            and dish_flavor = #{dishFlavor}

mapper.select:
select * from sky_take_out.shopping_cart


            and user_id = #{userId}


            and dish_id = #{dishId}


            and setmeal_id = #{setmealId}


            and dish_flavor = #{dishFlavor}
